import React from "react";
import PropTypes from "prop-types";
import ".MenuBar.css"; // Путь к твоим стилям для Item

const Item = ({ text, defaultIcon, hoverIcon, onClick, className }) => {
  return (
    <button className={`item-button ${className}`} onClick={onClick}>
      <span className="item-icon">
        {/* Используем <img> для отображения иконки */}
        <img src={defaultIcon} alt={text} width="24px" height="24" className="item-icon-img" />
      </span>
      <span className="item-text">{text}</span>
    </button>
  );
};

Item.propTypes = {
  text: PropTypes.string.isRequired,  // Текст кнопки
  defaultIcon: PropTypes.string.isRequired, // Путь к иконке по умолчанию
  hoverIcon: PropTypes.string.isRequired,   // Путь к иконке для состояния наведения
  onClick: PropTypes.func, // Обработчик клика
  className: PropTypes.string,  // Дополнительный класс для стилизации
};

Item.defaultProps = {
  onClick: () => {}, // Пустая функция по умолчанию
  className: ""
};

export default Item;
