import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Component from "../Component/Component";
import "./AddNewElement.css";

const AddNewElement = () => {
  const [containers, setContainers] = useState([]);
  const [nextId, setNextId] = useState(1);
  const [selectedIds, setSelectedIds] = useState([]); // Массив выбранных контейнеров
  const [deleteMode, setDeleteMode] = useState(false); // Режим удаления
  const navigate = useNavigate();

  // Функция добавления контейнера
  const handleAdd = () => {
    const newContainer = {
      id: nextId,
      description: `Container ${nextId}`,
    };
    setContainers([...containers, newContainer]);
    setNextId(nextId + 1);
  };

  // Функция выбора контейнера для удаления
  const handleSelect = (id) => {
    if (deleteMode) {
      // Если в режиме удаления, добавляем или убираем контейнер из выбранных
      setSelectedIds((prevSelectedIds) =>
        prevSelectedIds.includes(id)
          ? prevSelectedIds.filter((selectedId) => selectedId !== id)
          : [...prevSelectedIds, id]
      );
    }
  };

  // Функция редактирования (переход на страницу)
  const handleEdit = (id) => {
    navigate(`/edit/${id}`);
  };

  // Функция включения режима удаления
  const toggleDeleteMode = () => {
    setDeleteMode(!deleteMode);
    if (deleteMode) {
      // Если мы выходим из режима удаления, очищаем выбранные элементы
      setSelectedIds([]);
    }
  };

  // Функция удаления выбранных контейнеров
  const handleDelete = () => {
    setContainers(containers.filter((container) => !selectedIds.includes(container.id)));
    setSelectedIds([]); // Очищаем выбранные элементы
    setDeleteMode(false); // Выходим из режима удаления
  };

  return (
    <div>
      <div className="button-row">
        <button className="add-button" onClick={handleAdd}>+</button>
        <h1>Create a new component</h1>
        <button className="delete-button" onClick={toggleDeleteMode}>
          {deleteMode ? "Cancel" : "Delete"}
        </button>
        {deleteMode && (
          <button className="confirm-delete-button" onClick={handleDelete} disabled={selectedIds.length === 0}>
            Confirm Delete
          </button>
        )}
      </div>
      <div className="container-list">
        {containers.map((container) => (
          <Component
            key={container.id}
            number={container.id}
            description={container.description}
            onSelect={handleSelect}
            onEdit={handleEdit}
            isSelected={selectedIds.includes(container.id)} // Контейнер подсвечивается, если выбран
          />
        ))}
      </div>
    </div>
  );
};

export default AddNewElement;
